import React, { useEffect, useState } from 'react';
import ExhibitCard from '../components/ExhibitCard.js'; // Импортируем ExhibitCard
import ExhibitionCard from '../components/ExhibitionCard.js'; // Импортируем ExhibitionCard
import './Home.css'; // Импортируем стили для главной страницы

const Home = () => {
    const [exhibits, setExhibits] = useState([]);
    const [exhibitions, setExhibitions] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    async function fetchData () {
        try {
            const responseExhibits = await fetch('http://localhost:5000/api/exhibits');
            const responseExhibitions = await fetch('http://localhost:5000/api/exhibitions');

            if (!responseExhibits.ok || !responseExhibitions.ok) {
                throw new Error('Не удалось загрузить данные');
            }

            const exhibitsData = await responseExhibits.json();
            const exhibitionsData = await responseExhibitions.json();

            setExhibits(exhibitsData);
            setExhibitions(exhibitionsData);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    if (loading) {
        return <div className="loading">Загрузка...</div>;
    }

    if (error) {
        return <div className="error">Ошибка: {error}</div>;
    }

    return (
        <div className="home">
            <h2 className='main-title'>Добро пожаловать в музей</h2>
            <p>Откройте мир истории и культуры.</p>

            {/* Секция выставок */}
            <div className="section">
                <h3>Выставки</h3>
                <div className="exhibit-cards-container two-rows">
                    {exhibitions.slice(0, 4).map((exhibition) => ( // Выводим только первые 5 выставок
                        <ExhibitionCard key={exhibition._id} exhibition={exhibition} />
                    ))}
                </div>
            </div>

            {/* Секция экспонатов */}
            <div className="section">
                <h3>Экспонаты</h3>
                <div className="exhibit-cards-container two-rows">
                    {exhibits.slice(0, 5).map((exhibit) => ( // Выводим только первые 5 экспонатов
                        <ExhibitCard key={exhibit._id} exhibit={exhibit} />
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Home;
