import React, { useState } from "react";

const GenderizeComponent = () => {
  const [name, setName] = useState("luc"); // Имя по умолчанию
  const [genderData, setGenderData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const API_URL = `https://api.genderize.io/?name=${name}`; // URL API

  const fetchGenderData = async () => {
    setLoading(true);
    setError(null); // Сбрасываем ошибку перед новым запросом
    try {
      const response = await fetch(API_URL);
      
      // Проверка на успешный ответ
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setGenderData(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchGenderData(); // Запрос данных при отправке формы
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>Genderize API</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Enter a name"
          required
        />
        <button type="submit">Get Gender</button>
      </form>
      {loading && <div>Loading...</div>}
      {error && <div>Error: {error}</div>}
      {genderData && (
        <div>
          <h3>Result for: {genderData.name}</h3>
          <p><strong>Gender:</strong> {genderData.gender}</p>
          <p><strong>Probability:</strong> {genderData.probability}</p>
          <p><strong>Count:</strong> {genderData.count}</p>
        </div>
      )}
    </div>
  );
};

export default GenderizeComponent;
