import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Layout from './components/Layout';
import Home from './pages/Home';
import Login from './pages/Login';
import Profile from './pages/Profile';
import Register from './pages/Register';
import ExhibitionList from './components/ExhibitionList';
import ExhibitList from './components/ExhibitList';
import HallList from './components/HallList';
import EditExhibition from './components/EditExhibition';
import EditExhibit from './components/EditExhibit';
import EditHall from './components/EditHall';

const App = () => {
  const [exhibitions, setExhibitions] = useState([]);
  const [exhibits, setExhibits] = useState([]);
  const [halls, setHalls] = useState([]);

  return (
    <Router>
      <Layout>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/register" element={<Register />} />
          <Route path="/exhibitions" element={<ExhibitionList exhibitions={exhibitions} />} />
          <Route path="/exhibits" element={<ExhibitList exhibits={exhibits} />} />
          <Route path="/halls" element={<HallList halls={halls} />} />
          <Route path="/exhibitions/edit/:id" element={<EditExhibition exhibition={(id) => exhibitions.find(ex => ex._id === id)} />} />
          <Route path="/exhibits/edit/:id" element={<EditExhibit exhibit={(id) => exhibits.find(ex => ex._id === id)} />} />
          <Route path="/halls/edit/:id" element={<EditHall hall={(id) => halls.find(h => h._id === id)} />} />
        </Routes>
      </Layout>
    </Router>
  );
};

export default App;
