import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { observer } from 'mobx-react';
import './Header.css';

const Header = () => {
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const user = JSON.parse(localStorage.getItem('user'));

  const handleDropdownToggle = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setDropdownOpen(false);
    window.location.href = 'http://localhost:5000/api/logout';
  };

  const handleProfile = () => {
    window.location.href = 'http://localhost:3000/profile';
    setDropdownOpen(false);
  };

  return (
    <header className="header">
      <h1>Museum</h1>
      <nav>
        <ul>
          <li><Link to="/">Home</Link></li>
          {user ? (
            <li className="dropdown">
              <button 
                className="dropdown-toggle" 
                onClick={handleDropdownToggle}
                aria-expanded={isDropdownOpen}
              >
                {user.name}
              </button>
              {isDropdownOpen && (
                <ul className="dropdown-menu">
                  <li>
                    <Link onClick={handleProfile} to="/profile" className="dropdown-link">Profile</Link>
                  </li>
                  <li>
                    <button onClick={handleLogout} className="dropdown-link logout-button">Logout</button>
                  </li>
                </ul>
              )}
            </li>
          ) : (
            <li><Link to="/login">Login</Link></li>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default observer(Header);
