import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import './EditExhibit.css';

const EditExhibit = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const [exhibit, setExhibit] = useState({
    name: '',
    description: '',
    year: '',
    hall: '',
    creator: '',
    isActive: true,
  });
  const [loading, setLoading] = useState(!!id);
  const [error, setError] = useState(null);
  const [halls, setHalls] = useState([]); // State to store halls
  const token = localStorage.getItem('jwt');
  const isAuthorized = !!token;

  const isEditMode = location.pathname.includes('edit');
  const isAddMode = location.pathname.includes('add');

  useEffect(() => {
    const fetchHalls = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/halls'); // Fetch halls from the API
        if (!response.ok) {
          throw new Error('Не удалось загрузить залы');
        }
        const data = await response.json();
        setHalls(data); // Set halls to state
      } catch (error) {
        setError(error.message);
      }
    };

    fetchHalls(); // Fetch halls when the component mounts

    if (id && !isAddMode) {
      const fetchExhibit = async () => {
        try {
          const response = await fetch(`http://localhost:5000/api/exhibits/${id}`);
          if (!response.ok) {
            throw new Error('Не удалось загрузить экспонат');
          }
          const data = await response.json();
          setExhibit(data);
        } catch (error) {
          setError(error.message);
        } finally {
          setLoading(false);
        }
      };

      fetchExhibit();
    } else {
      setLoading(false);
    }
  }, [id, isAddMode]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const method = id ? 'PUT' : 'POST';
      const url = id
        ? `http://localhost:5000/api/exhibits/${id}`
        : 'http://localhost:5000/api/exhibits';
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(exhibit),
      });

      if (!response.ok) {
        throw new Error('Не удалось сохранить экспонат');
      }

      navigate('/exhibits');
    } catch (error) {
      setError(error.message);
    }
  };

  if (!isAuthorized && isAddMode && isEditMode) {
    return <div className="error">Вы не авторизованы для доступа к этой странице.</div>;
  }

  if (loading) {
    return <div className="loading">Загрузка...</div>;
  }

  if (error) {
    return <div className="error">Ошибка: {error}</div>;
  }

  return (
    <div className="edit-exhibit-container">
      <h2>{isAddMode ? 'Добавить экспонат' : `Редактировать экспонат: ${exhibit.name}`}</h2>
      <form onSubmit={handleSubmit} className="form">
        <label>
          Название:
          <input
            type="text"
            value={exhibit.name}
            onChange={(e) => setExhibit({ ...exhibit, name: e.target.value })}
            required
          />
        </label>
        <label>
          Описание:
          <textarea
            value={exhibit.description}
            onChange={(e) => setExhibit({ ...exhibit, description: e.target.value })}
          />
        </label>
        <label>
          Год:
          <input
            type="number"
            value={exhibit.year}
            onChange={(e) => setExhibit({ ...exhibit, year: e.target.value })}
            required
          />
        </label>
        <label>
          Зал:
          <select
            value={exhibit.hall}
            onChange={(e) => setExhibit({ ...exhibit, hall: e.target.value })}
            required
          >
            <option value="">Выберите зал</option>
            {halls.map((hall) => (
              <option key={hall._id} value={hall._id}>
                {hall.name}
              </option>
            ))}
          </select>
        </label>
        <label>
          Создатель:
          <input
            type="text"
            value={exhibit.creator}
            onChange={(e) => setExhibit({ ...exhibit, creator: e.target.value })}
            required
          />
        </label>
        <label>
          Активно:
          <input
            type="checkbox"
            checked={exhibit.isActive}
            onChange={(e) => setExhibit({ ...exhibit, isActive: e.target.checked })}
          />
        </label>
        <button type="submit" className="submit-button">
          {id ? 'Обновить экспонат' : 'Создать экспонат'}
        </button>
      </form>
    </div>
  );
};

export default EditExhibit;
