import React, { useState, useEffect } from "react";
import axios from "axios";

const WeatherComponent = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_URL = "https://api.weatherbit.io/v2.0/current"; // Используем HTTPS
  const API_KEY = "4e0273fd3ff7488493555a3205511908"; // Ваш API ключ
  const LATITUDE = "35.7721"; // Широта
  const LONGITUDE = "-78.63861"; // Долгота

  useEffect(() => {
    const fetchWeatherData = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`${API_URL}?lat=${LATITUDE}&lon=${LONGITUDE}&key=${API_KEY}&include=minutely`);
        console.log("API response:", response.data); // Логирование ответа
        setWeatherData(response.data);
      } catch (err) {
        console.error("API response error:", err.response); // Логирование ошибки
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchWeatherData();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>Current Weather in {weatherData.data[0].city_name}, {weatherData.data[0].state_code}</h2>
      {weatherData && weatherData.data && (
        <div>
          <h3>Temperature: {weatherData.data[0].temp} °C</h3>
          <p><strong>Humidity:</strong> {weatherData.data[0].rh}%</p>
          <p><strong>Wind Speed:</strong> {weatherData.data[0].wind_spd} m/s</p>
          <p><strong>Weather:</strong> {weatherData.data[0].weather.description}</p>
          <p><strong>AQI:</strong> {weatherData.data[0].aqi}</p>
          <p><strong>Clouds:</strong> {weatherData.data[0].clouds}%</p>
        </div>
      )}
    </div>
  );
};

export default WeatherComponent;
