// DropdownMenu.js
import React from 'react';
import { Link } from 'react-router-dom';
import './DropdownMenu.css';

const DropdownMenu = ({ isOpen, toggleDropdown, userName, onLogout, onProfile }) => {
  return (
    <div className={`dropdown ${isOpen ? 'show' : ''}`}>
      <button
        className="dropdown-toggle"
        onClick={toggleDropdown}
        aria-expanded={isOpen}
      >
        {userName}
      </button>
      {isOpen && (
        <ul className="dropdown-menu">
          <li>
            <Link onClick={onProfile} to="/profile" className="dropdown-link">Profile</Link>
          </li>
          <li>
            <Link to="/exhibitions/edit" className="dropdown-link">Edit Exhibitions</Link>
          </li>
          <li>
            <Link to="/exhibits/edit" className="dropdown-link">Edit Exhibits</Link>
          </li>
          <li>
            <Link to="/halls/edit" className="dropdown-link">Edit Halls</Link>
          </li>
          <li>
            <button onClick={onLogout} className="dropdown-link logout-button">Logout</button>
          </li>
        </ul>
      )}
    </div>
  );
};

export default DropdownMenu;
