import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import './EditHall.css'; // CSS файл для стилей

const EditHall = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();

  const [hall, setHall] = useState({
    name: '',
    capacity: '',
    location: '',
    isActive: true,
    exhibition: '', // Поле для выставки
  });

  const [loading, setLoading] = useState(!!id);
  const [error, setError] = useState(null);
  const [exhibitions, setExhibitions] = useState([]); // Состояние для выставок
  const token = localStorage.getItem('jwt');
  const isAuthorized = !!token;

  const isEditMode = location.pathname.includes('edit');
  const isAddMode = location.pathname.includes('add');

  useEffect(() => {
    const fetchExhibitions = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/exhibitions`);
        if (!response.ok) {
          throw new Error('Не удалось загрузить выставки');
        }
        const data = await response.json();
        setExhibitions(data);
      } catch (error) {
        setError(error.message);
      }
    };

    fetchExhibitions();

    if (id && !isAddMode) {
      const fetchHall = async () => {
        try {
          const response = await fetch(`http://localhost:5000/api/halls/${id}`);
          if (!response.ok) {
            throw new Error('Не удалось загрузить зал');
          }
          const data = await response.json();
          setHall(data);
        } catch (error) {
          setError(error.message);
        } finally {
          setLoading(false);
        }
      };

      fetchHall();
    } else {
      setLoading(false);
    }
  }, [id, isAddMode]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const method = id ? 'PUT' : 'POST';
      const url = id
        ? `http://localhost:5000/api/halls/${id}`
        : 'http://localhost:5000/api/halls';
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(hall),
      });

      if (!response.ok) {
        throw new Error('Не удалось сохранить зал');
      }

      navigate('/halls');
    } catch (error) {
      setError(error.message);
    }
  };

  if (!isAuthorized) {
    return <div className="error">Вы не авторизованы для доступа к этой странице.</div>;
  }

  if (loading) {
    return <div className="loading">Загрузка...</div>;
  }

  if (error) {
    return <div className="error">Ошибка: {error}</div>;
  }

  return (
    <div className="edit-hall-container">
      <div className="details-header">
        <h2>
          {isAddMode ? 'Добавить зал' : id ? `Зал: ${hall.name}` : ''}
          {isEditMode && !isAddMode && ' (Режим редактирования)'}
        </h2>
      </div>

      {(isEditMode || isAddMode) ? (
        <form onSubmit={handleSubmit} className="form">
          <label>
            Название:
            <input
              type="text"
              value={hall.name}
              onChange={(e) => setHall({ ...hall, name: e.target.value })}
              required
            />
          </label>
          <label>
            Вместимость:
            <input
              type="number"
              value={hall.capacity}
              onChange={(e) => setHall({ ...hall, capacity: e.target.value })}
              required
            />
          </label>
          <label>
            Локация:
            <input
              type="text"
              value={hall.location}
              onChange={(e) => setHall({ ...hall, location: e.target.value })}
              required
            />
          </label>
          <label>
            Активен:
            <input
              type="checkbox"
              checked={hall.isActive}
              onChange={(e) => setHall({ ...hall, isActive: e.target.checked })}
            />
          </label>
          <label>
            Выставка:
            <select
              value={hall.exhibition}
              onChange={(e) => setHall({ ...hall, exhibition: e.target.value })}
              required
            >
              <option value="">Выберите выставку</option>
              {exhibitions.map((exhibition) => (
                <option style={'color: black;'} key={exhibition._id} value={exhibition._id}>
                  {exhibition.name}
                </option>
              ))}
            </select>
          </label>
          <button type="submit" className="submit-button">
            {id ? 'Обновить зал' : 'Создать зал'}
          </button>
        </form>
      ) : (
        <div className="details">
          <p><strong>Название:</strong> {hall.name}</p>
          <p><strong>Вместимость:</strong> {hall.capacity}</p>
          <p><strong>Локация:</strong> {hall.location}</p>
          <p><strong>Активен:</strong> {hall.isActive ? 'Да' : 'Нет'}</p>
          <p><strong>Выставка:</strong> {hall.exhibition.name}</p>
        </div>
      )}
    </div>
  );
};

export default EditHall;
