import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import './EditHall.css'; // CSS file for styling

const EditHall = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  
  const [hall, setHall] = useState({
    name: '',
    capacity: '',
    location: '',
    isActive: true,
  });
  
  const [loading, setLoading] = useState(!!id);
  const [error, setError] = useState(null);
  const token = localStorage.getItem('jwt');
  const isAuthorized = !!token;

  const isEditMode = location.pathname.includes('edit');
  const isAddMode = location.pathname.includes('add');

  useEffect(() => {
    if (id && !isAddMode) {
      const fetchHall = async () => {
        try {
          const response = await fetch(`http://localhost:5000/api/halls/${id}`);
          if (!response.ok) {
            throw new Error('Failed to load hall');
          }
          const data = await response.json();
          setHall(data);
        } catch (error) {
          setError(error.message);
        } finally {
          setLoading(false);
        }
      };

      fetchHall();
    } else {
      setLoading(false);
    }
  }, [id, isAddMode]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const method = id ? 'PUT' : 'POST';
      const url = id
        ? `http://localhost:5000/api/halls/${id}`
        : 'http://localhost:5000/api/halls';
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(hall),
      });

      if (!response.ok) {
        throw new Error('Failed to save hall');
      }

      navigate('/halls');
    } catch (error) {
      setError(error.message);
    }
  };

  if (!isAuthorized) {
    return <div className="error">You are not authorized to access this page.</div>;
  }

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  return (
    <div className="edit-hall-container">
      <div className="details-header">
        <h2>
          {isAddMode ? 'Add Hall' : id ? `Hall: ${hall.name}` : ''}
          {isEditMode && !isAddMode && ' (Edit Mode)'}
        </h2>
      </div>

      {(isEditMode || isAddMode) ? (
        <form onSubmit={handleSubmit} className="form">
          <label>
            Name:
            <input
              type="text"
              value={hall.name}
              onChange={(e) => setHall({ ...hall, name: e.target.value })}
              required
            />
          </label>
          <label>
            Capacity:
            <input
              type="number"
              value={hall.capacity}
              onChange={(e) => setHall({ ...hall, capacity: e.target.value })}
              required
            />
          </label>
          <label>
            Location:
            <input
              type="text"
              value={hall.location}
              onChange={(e) => setHall({ ...hall, location: e.target.value })}
              required
            />
          </label>
          <label>
            Active:
            <input
              type="checkbox"
              checked={hall.isActive}
              onChange={(e) => setHall({ ...hall, isActive: e.target.checked })}
            />
          </label>
          <button type="submit" className="submit-button">
            {id ? 'Update Hall' : 'Create Hall'}
          </button>
        </form>
      ) : (
        <div className="details">
          <p><strong>Name:</strong> {hall.name}</p>
          <p><strong>Capacity:</strong> {hall.capacity}</p>
          <p><strong>Location:</strong> {hall.location}</p>
          <p><strong>Active:</strong> {hall.isActive ? 'Yes' : 'No'}</p>
        </div>
      )}
    </div>
  );
};

export default EditHall;
