import React from 'react';
import './ExhibitionCard.css';

const ExhibitionCard = ({ exhibition }) => {
    return (
        <div className="exhibition-card">
            <div className="exhibition-card-header">
                <h3>{exhibition.title}</h3>
            </div>
            <div className="exhibition-card-body">
                <p>{exhibition.description}</p>
                <p><strong>Дата начала:</strong> {new Date(exhibition.startDate).toLocaleDateString()}</p>
                <p><strong>Дата окончания:</strong> {new Date(exhibition.endDate).toLocaleDateString()}</p>
            </div>
            <div className="exhibition-card-footer">
                <button className="learn-more-button">Подробнее</button>
            </div>
        </div>
    );
};

export default ExhibitionCard;
