import React, { useEffect, useState } from 'react';
import ExhibitCard from '../components/ExhibitCard.js'; // Импортируем ExhibitCard
import './Home.css'; // Импортируем стили для главной страницы

const Home = () => {
    const [exhibits, setExhibits] = useState([]); // Данные экспонатов
    const [exhibitions, setExhibitions] = useState([]); // Данные выставок
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchData = async () => {
        try {
            const responseExhibits = await fetch('http://localhost:5000/api/exhibits');
            const responseExhibitions = await fetch('http://localhost:5000/api/exhibitions');

            if (!responseExhibits.ok || !responseExhibitions.ok) {
                throw new Error('Не удалось загрузить данные');
            }

            const exhibitsData = await responseExhibits.json();
            const exhibitionsData = await responseExhibitions.json();

            setExhibits(exhibitsData); // Сохраняем экспонаты
            setExhibitions(exhibitionsData); // Сохраняем выставки
        } catch (error) {
            setError(error.message); // Устанавливаем сообщение об ошибке
        } finally {
            setLoading(false); // Устанавливаем загрузку в false после завершения
        }
    };

    useEffect(() => {
        fetchData(); // Загружаем данные при загрузке компонента
    }, []);

    if (loading) {
        return <div className="loading">Загрузка...</div>; // Показать индикатор загрузки
    }

    if (error) {
        return <div className="error">Ошибка: {error}</div>; // Показать ошибку, если она возникла
    }

    return (
        <div className="home">
            <h2>Добро пожаловать в музей</h2>
            <p>Откройте мир истории и культуры.</p>

            {/* Секция выставок */}
            <div className="section">
                <h3>Выставки</h3>
                <div className="exhibit-cards-container">
                    {exhibitions.map((exhibition) => (
                        <ExhibitCard key={exhibition._id} exhibit={exhibition} />
                    ))}
                </div>
            </div>

            {/* Секция экспонатов */}
            <div className="section">
                <h3>Экспонаты</h3>
                <div className="exhibit-cards-container">
                    {exhibits.map((exhibit) => (
                        <ExhibitCard key={exhibit._id} exhibit={exhibit} />
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Home;
