import React from 'react';
import './ExhibitCard.css';

const ExhibitCard = ({ exhibit }) => {
    return (
        <div className="exhibit-card">
            <h3>{exhibit.name}</h3>
            <p>{exhibit.description}</p>
            <p><strong>Выставка:</strong> {exhibit.hall.name}</p>
            <p><strong>Дата создания:</strong> {new Date(exhibit.createdAt).toLocaleDateString()}</p>
            <p><strong>Дата обновления:</strong> {new Date(exhibit.updatedAt).toLocaleDateString()}</p>
        </div>
    );
};

export default ExhibitCard;
