import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './EditExhibition.css';

const EditExhibition = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [exhibition, setExhibition] = useState({
    title: '',
    description: '',
    startDate: '',
    endDate: '',
    isActive: true,
  });
  const [loading, setLoading] = useState(!!id);
  const [error, setError] = useState(null);
  const [viewMode, setViewMode] = useState('view');
  const token = localStorage.getItem('jwt');
  const isAuthorized = !!token;

  useEffect(() => {
    if (id) {
      const fetchExhibition = async () => {
        try {
          const response = await fetch(`http://localhost:5000/api/exhibitions/${id}`);
          if (!response.ok) {
            throw new Error('Failed to fetch exhibition');
          }
          const data = await response.json();
          setExhibition(data);
        } catch (error) {
          setError(error.message);
        } finally {
          setLoading(false);
        }
      };

      fetchExhibition();
    }
  }, [id]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const method = id ? 'PUT' : 'POST';
      const url = id
        ? `http://localhost:5000/api/exhibitions/${id}`
        : 'http://localhost:5000/api/exhibitions';
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(exhibition),
      });

      if (!response.ok) {
        throw new Error('Failed to save exhibition');
      }

      navigate('/exhibitions');
    } catch (error) {
      setError(error.message);
    }
  };

  if (!isAuthorized) {
    return <div className="error">You are not authorized to access this page.</div>;
  }

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  return (
    <div className="edit-exhibition-container">
      <div className="header">
        <h2>
          {id ? `Exhibition: ${exhibition.title}` : 'Add Exhibition'}
          {viewMode === 'view' && ' (View Mode)'}
        </h2>
        <button
          onClick={() => setViewMode(viewMode === 'edit' ? 'view' : 'edit')}
          className="toggle-button"
        >
          {viewMode === 'edit' ? 'View Details' : 'Edit'}
        </button>
      </div>

      {viewMode === 'edit' ? (
        <form onSubmit={handleSubmit} className="form">
          <label>
            Title:
            <input
              type="text"
              value={exhibition.title}
              onChange={(e) => setExhibition({ ...exhibition, title: e.target.value })}
              required
            />
          </label>
          <label>
            Description:
            <textarea
              value={exhibition.description}
              onChange={(e) => setExhibition({ ...exhibition, description: e.target.value })}
            />
          </label>
          <label>
            Start Date:
            <input
              type="date"
              value={exhibition.startDate}
              onChange={(e) => setExhibition({ ...exhibition, startDate: e.target.value })}
              required
            />
          </label>
          <label>
            End Date:
            <input
              type="date"
              value={exhibition.endDate}
              onChange={(e) => setExhibition({ ...exhibition, endDate: e.target.value })}
            />
          </label>
          <label>
            Active:
            <input
              type="checkbox"
              checked={exhibition.isActive}
              onChange={(e) => setExhibition({ ...exhibition, isActive: e.target.checked })}
            />
          </label>
          <button type="submit" className="submit-button">
            {id ? 'Update Exhibition' : 'Create Exhibition'}
          </button>
        </form>
      ) : (
        <div className="details">
          <p><strong>Title:</strong> {exhibition.title}</p>
          <p><strong>Description:</strong> {exhibition.description}</p>
          <p><strong>Start Date:</strong> {new Date(exhibition.startDate).toLocaleDateString()}</p>
          <p><strong>End Date:</strong> {new Date(exhibition.endDate).toLocaleDateString()}</p>
          <p><strong>Active:</strong> {exhibition.isActive ? 'Yes' : 'No'}</p>
        </div>
      )}
    </div>
  );
};

export default EditExhibition;
