import React, { useEffect } from 'react'; // Исправлено: useEffect из React
import logo from './logo.svg';
import './App.css';

function App() {
  // Тестовый запрос на API
  useEffect(() => {
    fetch("/api") // Прокси добавляет `/api` к http://localhost:5000
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log("Response from API:", data); // Выводим ответ API
      })
      .catch((error) => {
        console.error("Error fetching API:", error); // Логируем ошибки
      });
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
