import React, { useState, useEffect } from "react";
import axios from "axios";

const AirQualityComponent = () => {
  const [airQualityData, setAirQualityData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_URL = "https://api.weatherbit.io/v2.0/current/airquality";
  const API_KEY = "4e0273fd3ff7488493555a3205511908"; 
  const CITY = "Minsk";
  const COUNTRY = "BY"; // Код страны для Беларуси

  useEffect(() => {
    const fetchAirQualityData = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`${API_URL}?city=${CITY}&country=${COUNTRY}&key=${API_KEY}`);
        console.log("API response:", response.data); // Логирование ответа
        setAirQualityData(response.data);
      } catch (err) {
        console.error("API response error:", err.response); // Логирование ошибки
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchAirQualityData();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>Air Quality in Minsk, Belarus</h2>
      {airQualityData && airQualityData.data && (
        <div>
          <h3>AQI: {airQualityData.data[0].aqi}</h3>
          <p><strong>SO2:</strong> {airQualityData.data[0].so2} µg/m³</p>
          <p><strong>NO2:</strong> {airQualityData.data[0].no2} µg/m³</p>
          <p><strong>O3:</strong> {airQualityData.data[0].o3} µg/m³</p>
          <p><strong>PM2.5:</strong> {airQualityData.data[0].pm25} µg/m³</p>
          <p><strong>PM10:</strong> {airQualityData.data[0].pm10} µg/m³</p>
        </div>
      )}
    </div>
  );
};

export default AirQualityComponent;
