import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import Layout from './components/Layout';
import Home from './pages/Home';
import Login from './pages/Login';
import Profile from './pages/Profile';
import Register from './pages/Register';
import ExhibitionList from './components/ExhibitionList';
import ExhibitList from './components/ExhibitList';
import HallList from './components/HallList';
import EditExhibition from './components/EditExhibition';
import EditExhibit from './components/EditExhibit';
import EditHall from './components/EditHall';

const App = () => {
  const [exhibitions, setExhibitions] = useState([]);
  const [exhibits, setExhibits] = useState([]);
  const [halls, setHalls] = useState([]);
  return (
    <Router>
      <Layout>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/register" element={<Register />} />
        </Routes>
      </Layout>
      <Switch>
        <Route path="/exhibitions" exact>
          <ExhibitionList exhibitions={exhibitions} />
        </Route>
        <Route path="/exhibits" exact>
          <ExhibitList exhibits={exhibits} />
        </Route>
        <Route path="/halls" exact>
          <HallList halls={halls} />
        </Route>
        <Route path="/exhibitions/edit/:id" render={(props) => <EditExhibition exhibition={exhibitions.find(ex => ex._id === props.match.params.id)} />} />
        <Route path="/exhibits/edit/:id" render={(props) => <EditExhibit exhibit={exhibits.find(ex => ex._id === props.match.params.id)} />} />
        <Route path="/halls/edit/:id" render={(props) => <EditHall hall={halls.find(h => h._id === props.match.params.id)} />} />
      </Switch>
    </Router>
  );
};

export default App;
