import React from 'react';
import './DropdownMenu.css';

const DropdownMenu = ({ isOpen, toggleDropdown, userName, onLogout, onProfile }) => {
  return (
    isOpen && (
      <div className="dropdown-menu">
        <li className="account-name">{userName}</li> {/* Добавляем имя пользователя с классом */}
        <li><a href="#" className="dropdown-link" onClick={onProfile}>Profile</a></li>
        <li><button className="logout-button" onClick={onLogout}>Logout</button></li>
      </div>
    )
  );
};

export default DropdownMenu;
