import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { observer } from 'mobx-react';
import { jwtDecode as decodeJwt } from 'jwt-decode';
import DropdownMenu from './DropdownMenu'; // Импортируем новый компонент
import './Header.css';

const Header = () => {
  const [isDropdownOpen, setDropdownOpen] = useState(false);
  const [userName, setUserName] = useState(null);

  // Извлечение имени пользователя из JWT токена
  useEffect(() => {
    const token = localStorage.getItem('jwt');
    if (token) {
      try {
        const decodedToken = decodeJwt(token);
        setUserName(decodedToken.name);
      } catch (error) {
        console.error('Invalid token:', error);
        localStorage.removeItem('jwt');
      }
    }
  }, []);

  const handleDropdownToggle = () => {
    setDropdownOpen(!isDropdownOpen);
  };

  const handleLogout = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/logout', {
        method: 'GET',
      });

      if (response.ok) {
        localStorage.removeItem('jwt');
        document.cookie.split(';').forEach((cookie) => {
          document.cookie = cookie
            .replace(/^ +/, '')
            .replace(/=.*/, `=; expires=${new Date(0).toUTCString()}; path=/`);
        });
        window.location.href = '/login';
      } else {
        console.error('Failed to log out:', response.statusText);
      }
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  const handleProfile = () => {
    window.location.href = 'http://localhost:3000/profile';
    setDropdownOpen(false);
  };

  return (
    <header className="header">
      <h1>Museum</h1>
      <nav>
        <ul className="nav-links">
          <li><Link to="/">Home</Link></li>
          <li><Link to="/exhibitions">Exhibitions</Link></li>
          <li><Link to="/exhibits">Exhibits</Link></li>
          <li><Link to="/halls">Halls</Link></li>
        </ul>
        <ul className="auth-links">
          {userName ? (
            <li>
              <DropdownMenu
                isOpen={isDropdownOpen}
                toggleDropdown={handleDropdownToggle}
                userName={userName}
                onLogout={handleLogout}
                onProfile={handleProfile}
              />
            </li>
          ) : (
            <>
              <li><Link to="/login">Login</Link></li>
              <li><Link to="/register">Register</Link></li> {/* Добавляем ссылку на регистрацию */}
            </>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default observer(Header);
