import React, { useEffect, useState } from 'react';
import { observer } from 'mobx-react';
import './Login.css';
import { Link } from 'react-router-dom';
import Cookies from 'js-cookie';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    try {
        const response = await fetch('http://localhost:5000/api/auth/google', {
            method: 'GET',
            credentials: 'include' // Если нужно отправить cookie с запросом
        });

        if (!response.ok) {
            throw new Error('Google authentication failed');
        }

        const data = await response.json();
        const token = data.token; // Получаем токен из ответа
        const user = data.user; // Получаем информацию о пользователе

        // Сохраняем токен в localStorage или cookies
        localStorage.setItem('jwt', token);
        localStorage.setItem('user', JSON.stringify(user)); // Сохраняем информацию о пользователе

        // Перенаправляем на главную страницу или другую
        window.location.href = '/'; // Замените на ваш путь
    } catch (error) {
        console.error('Error during login:', error);
        alert('Login failed. Please try again.');
    }
};


  const handleFormSubmit = (e) => {
    e.preventDefault();
    const user = { email, password };
    console.log('User Login:', JSON.stringify(user));
    // Здесь может быть вызов API для авторизации
    // Например, с использованием fetch или axios:
    fetch('http://localhost:5000/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(user),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Login failed');
        }
        return response.json();
      })
      .then((data) => {
        Cookies.set('jwt', data.token);
        localStorage.setItem('user', JSON.stringify(data)); // Сохраняем токен пользователя
        window.location.href = '/';
      })
      .catch((error) => {
        console.error('Error:', error);
        alert('Login failed. Please check your email and password.');
      });
  };

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const token = params.get('token');
    if (token) {
        Cookies.set('jwt', token);
        window.location.href = '/';
    }
  }, []);

  return (
    <div className="login">
      <div className="login-form">
        <h2>Login</h2>
        <form onSubmit={handleFormSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              placeholder="Enter your email"
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Enter your password"
            />
          </div>
          <button type="submit" className="login-button">
            Login
          </button>
        </form>
        <div className="divider">or</div>
        <button onClick={handleLogin} className="google-login-button">
          Login with Google
        </button>
        <div className="register-link">
          <p>Don't have an account?</p>
          <Link to="/register">Register here</Link>
        </div>
      </div>
    </div>
  );
};

export default observer(Login);
