import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './ExhibitList.css'; // Подключение CSS

const ExhibitList = () => {
  const [exhibits, setExhibits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOrder, setSortOrder] = useState('asc');
  const navigate = useNavigate();

  // Проверка токена (имитация авторизации)
  useEffect(() => {
    const checkAuth = () => {
      const token = localStorage.getItem('jwt');
      setIsAuthenticated(!!token);
    };
    checkAuth();
  }, []);

  // Получение данных с API
  useEffect(() => {
    const fetchExhibits = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/exhibits');
        if (!response.ok) {
          throw new Error('Failed to fetch exhibits');
        }
        const data = await response.json();
        console.log(data);
        setExhibits(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchExhibits();
  }, []);

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this exhibit?')) {
      return;
    }

    try {
      const token = localStorage.getItem('jwt');
      const response = await fetch(`http://localhost:5000/api/exhibits/${id}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
      });
      if (!response.ok) {
        throw new Error('Failed to delete exhibit');
      }

      setExhibits(exhibits.filter((exhibit) => exhibit._id !== id));
    } catch (error) {
      setError(error.message);
    }
  };

  // Фильтрация выставок по поисковому запросу
// Фильтрация выставок по поисковому запросу
const filteredExhibits = exhibits.filter((exhibit) => 
    exhibit.title && exhibit.title.toLowerCase().includes(searchTerm.toLowerCase())
  );
  
  // Сортировка выставок
  const sortedExhibits = [...filteredExhibits].sort((a, b) => {
    const titleA = a.title ? a.title.toLowerCase() : '';
    const titleB = b.title ? b.title.toLowerCase() : '';
    
    return sortOrder === 'asc'
      ? titleA.localeCompare(titleB)
      : titleB.localeCompare(titleA);
  });
  

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="exhibit-list">
      <h2 className="title">Exhibits</h2>
      {isAuthenticated && (
        <button
          onClick={() => navigate('/exhibits/add')}
          className="add-button"
        >
          Add Exhibit
        </button>
      )}
      <div className="search-sort-container">
        <input
          type="text"
          placeholder="Search exhibits..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
        <select
          value={sortOrder}
          onChange={(e) => setSortOrder(e.target.value)}
          className="sort-select"
        >
          <option value="asc">Sort by Title (A-Z)</option>
          <option value="desc">Sort by Title (Z-A)</option>
        </select>
      </div>
      <ul className="list">
        {sortedExhibits.map((exhibit) => (
          <li key={exhibit._id} className="list-item">
            <span className="list-item-title">
              <Link to={`/exhibits/${exhibit._id}`}>{exhibit.title}</Link>
            </span>
            <div className="actions">
              {isAuthenticated && (
                <>
                  <button
                    className="btn edit"
                    onClick={() => navigate(`/exhibits/edit/${exhibit._id}`)}
                  >
                    Edit
                  </button>
                  <button
                    className="btn delete"
                    onClick={() => handleDelete(exhibit._id)}
                  >
                    Delete
                  </button>
                </>
              )}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ExhibitList;
