import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
// import './HallList.css'; // Create a CSS file for styling if needed

const HallList = () => {
  const [halls, setHalls] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchHalls = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/halls'); // Adjust API endpoint as necessary
        if (!response.ok) {
          throw new Error('Не удалось загрузить залы');
        }
        const data = await response.json();
        setHalls(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchHalls();
  }, []);

  if (loading) {
    return <div className="loading">Загрузка...</div>;
  }

  if (error) {
    return <div className="error">Ошибка: {error}</div>;
  }

  return (
    <div className="hall-list">
      <h2>Список залов</h2>
      <ul>
        {halls.map((hall) => (
          <li key={hall._id}>
            <Link to={`/halls/edit/${hall._id}`}>{hall.name}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default HallList;
