import React from 'react';
import './DropdownMenu.css';

const DropdownMenu = ({ isOpen, toggleDropdown, userName, onLogout, onProfile }) => {
  return (
    <div>
      <li className="account-name" onClick={toggleDropdown}>
        {userName}
      </li> {/* Имя пользователя теперь открывает дропдаун при клике */}
      {isOpen && (
        <div className="dropdown-menu">
          <li><a href="/profile" className="logout-button" onClick={onProfile}>Profile</a></li>
          <li><button className="logout-button" onClick={onLogout}>Logout</button></li>
        </div>
      )}
    </div>
  );
};

export default DropdownMenu;
